SUBROUTINE SORT_TEST_INT32_AS_WILD(UT)
  USE, INTRINSIC:: ISO_FORTRAN_ENV, ONLY: INT32
  USE RANDOM_ROUTINES,              ONLY: RANDOM_GENERATOR
  USE UNIT_TESTING_INSTRUCTION,     ONLY: UNIT_TESTS
  USE SORT_INTERFACE
  IMPLICIT NONE
  TYPE(UNIT_TESTS), INTENT(INOUT):: UT
  !
  TYPE(RANDOM_GENERATOR):: rg
  INTEGER(INT32), DIMENSION(4096):: A, B, C
  INTEGER,        DIMENSION(4096):: IA, IB, IC
  INTEGER:: I, J
  LOGICAL:: NO_ERROR
  !
  CALL UT%NEXT_TEST("1D Tests - INT32 as WILD")
  !
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  DO I = 1, SIZE(IC)
                 IC(i) = i
  END DO
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Reg Sort
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i) = C(:i)
       B(:i) = C(:i)
       CALL SORT(A(:i), ISWAP, ILESS)
       call ISORT(I, B(:i))
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), ISWAP, ILESS)')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Stable Sort
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i) = C(:i)
       B(:i) = C(:i)
       CALL SORT(A(:i), ISWAP, ILESS, STABLE=.TRUE.)
       call ISORT(I, B(:i))
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), ISWAP, ILESS, STABLE=.TRUE.)')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Stable Sort with Permutation Vector
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = C(:i)
       IA(:i) = IC(:i)
       IB(:i) = IC(:i)
       CALL SORT(A(:i), ISWAP, ILESS, STABLE=.TRUE., P=IA)
       call ISORT_IDX(I, B(:i), IB)
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
       if (.NOT. ALL(IA(:i) == IB(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), ISWAP, ILESS, ISWAP, ILESS, STABLE=.TRUE., P=IA)')
  !
  !##############################################################################################
  !
  ! Reg QSort
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i) = C(:i)
       B(:i) = C(:i)
       CALL SORT(A(:i), ISWAP, ILESS, QSORT=.TRUE.)
       call ISORT(I, B(:i))
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), ISWAP, ILESS, QSORT=.TRUE.)')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! QuickSort with Permutation Vector
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = C(:i)
       IA(:i) = IC(:i)
       IB(:i) = IC(:i)
       CALL SORT(A(:i), ISWAP, ILESS, QSORT=.TRUE., P=IA)
       call ISORT_IDX(I, B(:i), IB)
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), ISWAP, ILESS, STABLE=.TRUE., HEAP=.TRUE., P=IA)')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Reg HEAPSort
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i) = C(:i)
       B(:i) = C(:i)
       CALL SORT(A(:i), ISWAP, ILESS, HEAP=.TRUE.)
       call ISORT(I, B(:i))
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), ISWAP, ILESS, HEAP=.TRUE.)')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! HEAPSort with Permutation Vector
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = C(:i)
       IA(:i) = IC(:i)
       IB(:i) = IC(:i)
       CALL SORT(A(:i), ISWAP, ILESS, HEAP=.TRUE., P=IA)
       call ISORT_IDX(I, B(:i), IB)
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), ISWAP, ILESS, STABLE=.TRUE., HEAP=.TRUE., P=IA)')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Reg SymMerge
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i) = C(:i)
       B(:i) = C(:i)
       CALL SORT(A(:i), ISWAP, ILESS, SymMerge=.TRUE.)
       call ISORT(I, B(:i))
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), ISWAP, ILESS, SymMerge=.TRUE.)')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! SymMerge with Permutation Vector
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = C(:i)
       IA(:i) = IC(:i)
       IB(:i) = IC(:i)
       CALL SORT(A(:i), ISWAP, ILESS, SymMerge=.TRUE., P=IA)
       call ISORT_IDX(I, B(:i), IB)
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
       if (.NOT. ALL(IA(:i) == IB(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), ISWAP, ILESS, SymMerge=.TRUE., P=IA)')
  !
  !##############################################################################################
  CONTAINS
  !
  PURE SUBROUTINE ISORT(DIM,A)
    INTEGER,                       intent(in   ):: DIM
    INTEGER(INT32), dimension(dim),intent(inout):: A
    INTEGER(INT32):: TMP
    INTEGER:: I, J
    !
    DO I=2, DIM
        TMP = A(I)
        J   = I - 1
        DO WHILE ( J >= 1 )
                   IF( TMP >= A(J) ) EXIT
                   A(J+1) = A(J)
                   J = J - 1
        END DO
        A(J+1) = TMP
    END DO
    !
  END SUBROUTINE 
  !
  !--------------------------------------------------------------------------------------------------------------------------
  ! ISORT with Index Position Array that keeps track of where value origianted from
  !
  PURE SUBROUTINE ISORT_IDX(DIM,A,P)
    INTEGER,                       intent(in   ):: DIM
    INTEGER(INT32), dimension(DIM),intent(inout):: A   ! Array to be sorted
    INTEGER,        dimension(DIM),intent(inout):: P   ! Index Position Array
    INTEGER(INT32):: TMP
    INTEGER:: POS
    INTEGER:: I, J
    !
    DO I=2, DIM
        POS = P(I)
        TMP = A(I)
        J   = I - 1
        DO WHILE ( J >= 1 )
                   IF( TMP >= A(J) ) EXIT
                   A(J+1) = A(J)
                   P(J+1) = P(J)
                   J = J - 1
        END DO
        A(J+1) = TMP
        P(J+1) = POS
    END DO
    !
  END SUBROUTINE
  !
  PURE SUBROUTINE ISORT_DSC(DIM,A)
    INTEGER,                       intent(in   ):: DIM
    INTEGER(INT32), dimension(dim),intent(inout):: A
    INTEGER(INT32):: TMP
    INTEGER:: I, J
    !
    DO I=2, DIM
        TMP = A(I)
        J   = I - 1
        DO WHILE ( J >= 1 )
                   IF( TMP <= A(J) ) EXIT
                   A(J+1) = A(J)
                   J = J - 1
        END DO
        A(J+1) = TMP
    END DO
    !
  END SUBROUTINE 
  !
  !--------------------------------------------------------------------------------------------------------------------------
  ! ISORT with Index Position Array that keeps track of where value origianted from
  !
  PURE SUBROUTINE ISORT_IDX_DSC(DIM,A,P)
    INTEGER,                       intent(in   ):: DIM
    INTEGER(INT32), dimension(DIM),intent(inout):: A   ! Array to be sorted
    INTEGER,        dimension(DIM),intent(inout):: P   ! Index Position Array
    INTEGER(INT32):: TMP
    INTEGER:: POS
    INTEGER:: I, J
    !
    DO I=2, DIM
        POS = P(I)
        TMP = A(I)
        J   = I - 1
        DO WHILE ( J >= 1 )
                   IF( TMP <= A(J) ) EXIT
                   A(J+1) = A(J)
                   P(J+1) = P(J)
                   J = J - 1
        END DO
        A(J+1) = TMP
        P(J+1) = POS
    END DO
    !
  END SUBROUTINE
  !
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  !
  PURE SUBROUTINE ISWAP(dim, A, i, j)              ! Defining the ABSTRACT SWAP_METHOD Interface
      INTEGER,                 intent(in   ) :: dim, i, j
      CLASS(*),dimension(dim), intent(inout) :: A
      INTEGER(INT32) :: TMP 
      !
      SELECT TYPE(A)
      TYPE IS( INTEGER(INT32) )
                    TMP  = A(i)
                    A(i) = A(j)
                    A(j) = TMP
      END SELECT
  END SUBROUTINE
  !
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  !
  PURE FUNCTION ILESS(dim, A, i, j) RESULT(CMP)              ! Defining the ABSTRACT LESS_METHOD Interface
      INTEGER,                  intent(in) :: dim, i, j
      CLASS(*), dimension(dim), intent(in) :: A
      LOGICAL :: CMP
      
      SELECT TYPE(A)
      TYPE IS( INTEGER(INT32) )
                    CMP = A(i) < A(j)       ! To get ascending order flip less than sign
      END SELECT
  END FUNCTION
END SUBROUTINE    
