SUBROUTINE SORT_1D_TEST_INT32(UT)
  USE, INTRINSIC:: ISO_FORTRAN_ENV, ONLY: INT32
  USE RANDOM_ROUTINES,              ONLY: RANDOM_GENERATOR
  USE UNIT_TESTING_INSTRUCTION,     ONLY: UNIT_TESTS
  USE SORT_INTERFACE
  IMPLICIT NONE
  TYPE(UNIT_TESTS), INTENT(INOUT):: UT
  !
  TYPE(RANDOM_GENERATOR):: rg
  INTEGER(INT32), DIMENSION(4096):: A, B, C
  INTEGER,        DIMENSION(4096):: IA, IB, IC
  INTEGER:: I, J, K
  LOGICAL:: NO_ERROR
  !
  CALL UT%NEXT_TEST("1D Tests - int32")
  !
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  DO I = 1, SIZE(IC)
                 IC(i) = i
  END DO
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Reg Sort
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i) = C(:i)
       B(:i) = C(:i)
       CALL SORT(A(:i))
       call ISORT(I, B(:i))
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i))')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Stable Sort
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i) = C(:i)
       B(:i) = C(:i)
       CALL SORT(A(:i), STABLE=.TRUE.)
       call ISORT(I, B(:i))
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), STABLE=.TRUE.)')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Stable Sort with Permutation Vector
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = C(:i)
       IA(:i) = IC(:i)
       IB(:i) = IC(:i)
       CALL SORT(A(:i), STABLE=.TRUE., P=IA)
       call ISORT_IDX(I, B(:i), IB)
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
       if (.NOT. ALL(IA(:i) == IB(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(A(:i), STABLE=.TRUE., P=IA)')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Direct QuickSort Call
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i) = C(:i)
       B(:i) = C(:i)
       CALL SORT(i, A(:i))
       call ISORT(I, B(:i))
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(i, A(:i))')
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Direct SYMMERG Call
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = C(:i)
       IA(:i) = IC(:i)
       IB(:i) = IC(:i)
       CALL SORT(i, A(:i), IA)
       call ISORT_IDX(I, B(:i), IB)
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
       if (.NOT. ALL(IA(:i) == IB(:i))) then
           no_error = .false.
           exit
       end if
    END DO
    IF(.NOT. NO_ERROR) EXIT
  END DO
  CALL UT%ASSERT(NO_ERROR, MSG='CALL SORT(i, A(:i), IA)')
  !
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Descend Sort
  !
  NO_ERROR = .TRUE.
  DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i) = C(:i)
       B(:i) = C(:i)
       CALL SORT(A(:i), DESCEND=.TRUE.)
       call ISORT_DSC(I, B(:i))
       if (.NOT. ALL(A(:i) == B(:i))) then
           no_error = .false.
           exit
       end if
    END DO
