  PURE SUBROUTINE ISORT(DIM,A)
    INTEGER,                       intent(in   ):: DIM
    INTEGER(INT32), dimension(dim),intent(inout):: A
    INTEGER(INT32):: TMP
    INTEGER:: I, J
    !
    DO I=2, DIM
        TMP = A(I)
        J   = I - 1
        DO WHILE ( J >= 1 )
                   IF( TMP >= A(J) ) EXIT
                   A(J+1) = A(J)
                   J = J - 1
        END DO
        A(J+1) = TMP
    END DO
    !
  END SUBROUTINE 
  !
  !--------------------------------------------------------------------------------------------------------------------------
  ! ISORT with Index Position Array that keeps track of where value origianted from
  !
  PURE SUBROUTINE ISORT_IDX(DIM,A,P)
    INTEGER,                       intent(in   ):: DIM
    INTEGER(INT32), dimension(DIM),intent(inout):: A   ! Array to be sorted
    INTEGER,        dimension(DIM),intent(inout):: P   ! Index Position Array
    INTEGER(INT32):: TMP
    INTEGER:: POS
    INTEGER:: I, J
    !
    DO I=2, DIM
        POS = P(I)
        TMP = A(I)
        J   = I - 1
        DO WHILE ( J >= 1 )
                   IF( TMP >= A(J) ) EXIT
                   A(J+1) = A(J)
                   P(J+1) = P(J)
                   J = J - 1
        END DO
        A(J+1) = TMP
        P(J+1) = POS
    END DO
    !
  END SUBROUTINE
  !
  PURE SUBROUTINE ISORT_DSC(DIM,A)
    INTEGER,                       intent(in   ):: DIM
    INTEGER(INT32), dimension(dim),intent(inout):: A
    INTEGER(INT32):: TMP
    INTEGER:: I, J
    !
    DO I=2, DIM
        TMP = A(I)
        J   = I - 1
        DO WHILE ( J >= 1 )
                   IF( TMP <= A(J) ) EXIT
                   A(J+1) = A(J)
                   J = J - 1
        END DO
        A(J+1) = TMP
    END DO
    !
  END SUBROUTINE 
  !
  !--------------------------------------------------------------------------------------------------------------------------
  ! ISORT with Index Position Array that keeps track of where value origianted from
  !
  PURE SUBROUTINE ISORT_IDX_DSC(DIM,A,P)
    INTEGER,                       intent(in   ):: DIM
    INTEGER(INT32), dimension(DIM),intent(inout):: A   ! Array to be sorted
    INTEGER,        dimension(DIM),intent(inout):: P   ! Index Position Array
    INTEGER(INT32):: TMP
    INTEGER:: POS
    INTEGER:: I, J
    !
    DO I=2, DIM
        POS = P(I)
        TMP = A(I)
        J   = I - 1
        DO WHILE ( J >= 1 )
                   IF( TMP <= A(J) ) EXIT
                   A(J+1) = A(J)
                   P(J+1) = P(J)
                   J = J - 1
        END DO
        A(J+1) = TMP
        P(J+1) = POS
    END DO
    !
  END SUBROUTINE
