SUBROUTINE SORT_1D_1D_INT32_INT32_TEST(UT)
  USE, INTRINSIC:: ISO_FORTRAN_ENV, ONLY: INT32
  USE UNIT_TESTING_INSTRUCTION,     ONLY: UNIT_TESTS
  USE RANDOM_ROUTINES,              ONLY: RANDOM_GENERATOR
  USE SORT_INTERFACE
  IMPLICIT NONE
  TYPE(UNIT_TESTS), INTENT(INOUT):: UT
  !
  TYPE(RANDOM_GENERATOR):: rg
  INTEGER(INT32), DIMENSION(4096):: A, C
  INTEGER(INT32), DIMENSION(4096):: B, D
  INTEGER,        DIMENSION(4096):: IA
  INTEGER:: I, J, K
  !
  CALL UT%NEXT_TEST("Multi INT32-INT32 Tests", .true.)
  !
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Reg Sort
  !
  T1: DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    call rg%get(D, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = D(:i)
       !
       CALL SORT(A(:i), B(:i))
       !
       do k=1, i-1
           if(A(k) == A(k+1)) then
               if(B(k) > B(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i)) -> B not sorted')
                                 exit T1
               end if
           elseif(A(k) > A(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i)) -> A not sorted')
                                 exit T1
           end if
       end do
    END DO
  END DO T1
  !
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Reg DESCEND Sort
  !
  T2: DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    call rg%get(D, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = D(:i)
       !
       CALL SORT(A(:i), B(:i), DESCEND=.TRUE.)
       !
       do k=1, i-1
           if(A(k) == A(k+1)) then
               if(B(k) < B(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i)) -> B not sorted')
                                 exit T2
               end if
           elseif(A(k) < A(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i)) -> A not sorted')
                                 exit T2
           end if
       end do
    END DO
  END DO T2
  !
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Reg DESCEND2 Sort
  !
  T3: DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    call rg%get(D, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = D(:i)
       !
       CALL SORT(A(:i), B(:i), DESCEND=[.TRUE., .FALSE.])
       !
       do k=1, i-1
           if(A(k) == A(k+1)) then
               if(B(k) > B(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i)) -> B not sorted')
                                 exit T3
               end if
           elseif(A(k) < A(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i)) -> A not sorted')
                                 exit T3
           end if
       end do
    END DO
  END DO T3
  !
  !~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  ! Reg Sort with P
  !
  T4: DO J = 1, 10
    call rg%get(C, 1, 10+MODULO(J*3,30))
    call rg%get(D, 1, 10+MODULO(J*3,30))
    DO I = 36, SIZE(C), 7
       A(:i)  = C(:i)
       B(:i)  = D(:i)
       !
       CALL SORT(A(:i), B(:i), P=IA)
       !
       do k=1, i-1
           if(A(k) == A(k+1)) then
               if(B(k) > B(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i), P=IA) -> B not sorted')
                                 exit T4
               end if
           elseif(A(k) > A(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i), P=IA) -> A not sorted')
                                 exit T4
           end if
       end do
       A(:i)  = C(:i)
       B(:i)  = D(:i)
       CALL PERMUTATION_SORT(A(:i), IA)
       CALL PERMUTATION_SORT(B(:i), IA)
       !
       do k=1, i-1
           if(A(k) == A(k+1)) then
               if(B(k) > B(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i), P=IA) -> CALL PERMUTATION_SORT(A, IA)')
                                 exit T4
               end if
           elseif(A(k) > A(k+1)) then
                                 CALL UT%FAIL(MSG='CALL SORT(A(:i), B(:i), P=IA) -> CALL PERMUTATION_SORT(B, IA)')
